    static Map getHead() throws IOException {
        Map<String, String> headMap = new HashMap<>();

        String timeSpan = String.valueOf(System.currentTimeMillis() / 1000);
        String token = DigestUtils.md5Hex(APP_ID.concat(timeSpan).concat(APP_SECRET)).toUpperCase();

        headMap.put("Token", token);
        headMap.put("Timespan", timeSpan);

        return headMap;
    }

    static String encode(String content) throws UnsupportedEncodingException {
        return URLEncoder.encode(content, "UTF-8");
    }
